package cern.molr.commons.api.response;

import com.fasterxml.jackson.annotation.JsonProperty;

/**
 * A response bean containing the mission ID generated by MolR server when dealing with a client instantiation request.
 * The bean is sent back to the client.
 *
 * @author yassine-kr
 */
public final class InstantiationResponseBean {

    private final String missionId;

    public InstantiationResponseBean(@JsonProperty("missionId") String missionId) {
        this.missionId = missionId;
    }

    public String getMissionId() {
        return missionId;
    }

    @Override
    public boolean equals(Object o) {
        if (this == o) {
            return true;
        }
        if (o == null || getClass() != o.getClass()) {
            return false;
        }
        InstantiationResponseBean responseBean = (InstantiationResponseBean) o;

        return !(missionId != null ? !missionId.equals(responseBean.missionId) : responseBean.missionId != null);

    }

    @Override
    public int hashCode() {
        return missionId != null ? missionId.hashCode() : 0;
    }

}
